mapper.select:
select * from ( select A.qna_seq ,nvl(B.username, 'noname') username ,A.userid ,A.title ,A.contents --내용 ,nvl(A.hit , 0) hit --조회수 ,A.group_id ,A.depth ,A.g_level ,to_char(A.regdate, 'yyyy-mm-dd') regdate ,to_char(A.moddate, 'yyyy-mm-dd') moddate ,A.ip_addr ,row_number() over (order by A.group_id desc, A.g_level asc) as num ,ceil(row_number () over (order by A.group_id desc, A.g_level asc)/16-1) as pg from tl_question_answer A left outer join tl_member B on A.userid=B.userid where delyn='N'
and (title like '%'||#{key}||'%' or contents like '%'||#{key}||'%')
and title like '%'||#{key}||'%'
and contents like '%'||#{key}||'%'
)where pg=#{pg}

      
	select count(*) as isUpdate 
	from tl_like 
	where userid = #{userid} and target_id = #{target_id}

mapper.update:
	   
		update tl_like 
		 set   like_type = #{like_type}
		       ,datetime = sysdate
		       ,ip_addr = #{ip_addr}
		       ,delyn = case when #{like_type} = (select like_Type from tl_like where userid = #{userid} and target_id=#{target_id} and delyn='N') then 'Y'
		            when #{like_type} = (select like_Type from tl_like where userid = #{userid} and target_id=#{target_id} and delyn='Y') then 'N'
		            else 'N'
		            end    
		where userid = #{userid} and target_id=#{target_id}

update	tl_question_answer
set delyn='Y'
where qna_seq=#{qna_seq}

mapper.select:
select
	userid from tl_question_answer
	where qna_seq=#{qna_seq}
